import React, { useRef, useState } from 'react'

const Form = (props) => {
  const [state, setState] = useState('')
  const ref = useRef(null)

  function UpdateState() {
    // update init value of input
    const inputValue = ref.current.value
    // change State to new value
    setState(inputValue)
    // to get new value of input and make chage on props.setName with new value
    props.setName(inputValue)
  }

  return (
    <form className='form'>
      <div className='form-groub'>
        <input
          ref={ref}
          value={state}
          type='text'
          placeholder='enter name'
          onChange={() => UpdateState()}
        />
      </div>
      <div className='form-groub'>
        <input
          type='text'
          placeholder='enter email'
          onChange={() => props.setEmail('this')}
        />
      </div>
      <div className='form-groub'>
        <input type='submit' placeholder='add user' />
      </div>
      <p>
        your email:
        {props.email}
      </p>
    </form>
  )
}

export default Form
